<?

namespace ITHive\OpenLinesAdditional;

use \Bitrix\Main\Loader,
    \Bitrix\Main\Config\Option;


class Handler
{
    public static function afterMsgSendHandler($messageID, &$arFields)
    {
        /*
         * внесение записи id чата ОЛ в таблицу
         */
        
        if(!empty($arFields['CHAT_ENTITY_ID'])){
            $arChatId = explode('|', $arFields['CHAT_ENTITY_ID']);
            $chatId = $arChatId[2];
            $chatEntity = $arChatId[0];

            if($chatEntity == 'ithive_connect'){
                $chatFilter = [
                    "CHAT_ID" => $chatId,
                    "SYSTEM_TYPE" => 'ST',
                ];
            }else{
                $chatFilter = [
                    "CHAT_ID" => $chatId,
                    "SYSTEM_TYPE" => 'VK',
                ];
            }


            $existChat = Actions::getChatInfo($chatFilter);

            if ($existChat["ID"] and empty($existChat["OL_CHAT_ID"])) {
                $arPostFields = array(
                    "OL_CHAT_ID" => $arFields['TO_CHAT_ID'],
                    "CREATED" => new \Bitrix\Main\Type\DateTime()
                );
                OlTable::Update($existChat["ID"], $arPostFields);
            }

        }

        if ($arFields['CHAT_AUTHOR_ID'] == '0' or $arFields['SYSTEM']  == 'Y') return;


        /*
        *  три проверки - если сообщение отправил Клиент, то ничего не делаем или системное сообщение
        */
        if ($arFields['FROM_USER_ID'] != '0'){
            $rsUser = \CUser::GetByID($arFields['FROM_USER_ID']);
            $arUser = $rsUser->Fetch();
            $imconnector = explode('_', $arUser['LOGIN']);
            if($imconnector[0] == 'imconnector'){
                return;
            }
        }

        if(empty($arFields["MESSAGE"])){
            return;
        }

        if (Loader::includeModule('ithive.openlinesadditional')) {

            /*
             * ищем данные о посте во внешней системе по ID чата
            */
            $chatFilter = ["CHAT_ID" => $chatId];
            $chatSelect = ["POST_ID", "COMMENT_ID", "SYSTEM_TYPE",'USER_ID',"CHAT_ID"];
            $arChat = Actions::getChatInfo($chatFilter, $chatSelect);


            /*
             * обработка отправки данных во внешнюю систему
            */

            $access_key = '';
            switch ($arChat["SYSTEM_TYPE"]) {
                /*
                 * отправка данных в VK
                */
                case "VK":
                    $params = array(
                        "message" => $arFields["MESSAGE"],
                        "owner_id" => Option::get('ithive.openlinesadditional','field_id_grup'),
                        "post_id" => $arChat["CHAT_ID"],
                        "from_group" => abs(Option::get('ithive.openlinesadditional','field_id_grup')),
                        "reply_to_comment" => $arChat["POST_ID"],
                        'guid' => mt_rand(20, 99999999)
                    );
                    if(!empty($arFields["MESSAGE"])){
                        $res = Vkattachment::messagesSend($params);
                        
                    }
                    
                    break;
                /*
                 * отправка данных на сайте ВкусВилл (Drupal)
                */
                case "ST":
                    $params = array(
                        "message" => $arFields["MESSAGE"],
                        "post_id" => $arChat["CHAT_ID"],
                        "reply_to_comment" => $arChat["POST_ID"]
                    );
                    $action = Actions::preparSendToSite($params);
                    
                    break;
                /*
                 * обработка данных по-умолчанию
                */
                case "default":
                    break;

            }

          }
        unset($messageID,$arChat,$arFields);
    }

    public static function OnAfterFileUpload(&$arFields){

        /*
         * 2 проверки - если сообщение отправил Клиент, то ничего не делаем или системное сообщение
        */
        if ($arFields['FROM_USER_ID'] != '0'){
            $rsUser = \CUser::GetByID($arFields['FROM_USER_ID']);
            $arUser = $rsUser->Fetch();
            $imconnector = explode('_', $arUser['LOGIN']);
            if($imconnector[0] == 'imconnector'){
                return;
            }
        }

        if ($arFields['CHAT_AUTHOR_ID'] == '0' or $arFields['SYSTEM']  == 'Y') return;

        $chatId = trim($arFields['CHAT_ID']);

        $idObj = $arFields['FILE_ID'];

        $chatFilter = ["OL_CHAT_ID" => $chatId];

        $chatSelect = ["CHAT_ID", "POST_ID","OL_CHAT_ID", 'USER_ID', "COMMENT_ID", "SYSTEM_TYPE"];

        $arChat = Actions::getChatInfo($chatFilter, $chatSelect);


        if($arChat['SYSTEM_TYPE'] == 'VK'){

            $attachments = Vkattachment::prepareAttache($idObj);

            //удаляем копию файла из базы и физически
            if(!empty($attachments['attachments'])){
                \CFile::Delete($attachments['fileCopy']);

                $params = array(
                    'owner_id' => Option::get('ithive.openlinesadditional','field_id_grup'),
                    'post_id' => $arChat["CHAT_ID"],
                    'from_group' => abs(Option::get('ithive.openlinesadditional','field_id_grup')),
                    'reply_to_comment' => $arChat["POST_ID"],
                    'attachment' => $attachments['attachments'],
                    'guid' => mt_rand(20, 99999999)

                );
                $res = Vkattachment::messagesSend($params);

            }

        }

        unset($arChat,$arFields);

    }

